function convertToPx(e){if(e.includes("rem")||e.includes("em")){const t=getComputedStyle(document.querySelector(":root")).getPropertyValue("--convert-unit")||16;e=parseInt(e)*t}return e}const headerEl=document.querySelector(".header"),headerNextEl=headerEl.nextElementSibling,headerElHeight=headerEl.offsetHeight,cssHeaderHeight=getComputedStyle(document.querySelector(":root")).getPropertyValue("--header-height");document.querySelector(":root").style.setProperty("--header-height",`${headerElHeight>convertToPx(cssHeaderHeight)?headerElHeight+"px":cssHeaderHeight}`),window.addEventListener("scroll",(()=>{window.scrollY>parseInt(headerNextEl.offsetTop+50)?headerEl.classList.add("bg"):headerEl.classList.remove("bg")}));