const menuBtn=document.querySelector(".menu-btn"),menu=document.querySelector(".menu"),allMenuLinks=menu.querySelectorAll("a"),headerEl=document.querySelector(".header");function menuHandler(){menuBtn.classList.toggle("active"),menu.classList.toggle("active"),headerEl.classList.toggle("active")}function closeMenu(){menuBtn.classList.remove("active"),menu.classList.remove("active"),headerEl.classList.remove("active")}menuBtn.addEventListener("click",menuHandler),allMenuLinks.forEach((e=>{e.addEventListener("click",closeMenu)}));